{% set banner_home = getBannerByPositionCode('BANNER_HOME') %} {% if banner_home is not empty %}
    {% for b in banner_home %}
        <a {{ b.target }} href='{{ b.viewLink }}' title='{{ b.name }}'>
            <img loading='lazy' src='{{ b.imageSrc }}' alt='{{ b.name }}'>
        </a>
    {% endfor %}
{% endif %}
